<input type="number" class="input-text" name="<?php echo esc_attr( isset( $field['name'] ) ? $field['name'] : $key ); ?>" id="<?php echo esc_attr( $key ); ?>" value="<?php echo isset( $field['value'] ) ? esc_attr( $field['value'] ) : '1'; ?>" min="<?php echo ! empty( $field['min'] ) ? $field['min'] : '1'; ?>" max="<?php echo ! empty( $field['max'] ) ? $field['max'] : '10'; ?>" <?php if ( ! empty( $field['required'] ) ) echo 'required'; ?> />
<?php if ( ! empty( $field['description'] ) ) : ?><p><small class="description"><?php echo $field['description']; ?></small></p><?php endif; ?>